clear all;close all;clc;pkg load image;gry = double(rgb2gray(imread("lena.png")));% Show Original Imagefigureimshow(uint8(gry));title("Original Image");% Convert into Freq Domainfreq_gry = fft2(gry);freq_gry = fftshift(freq_gry);% Show image in freq domainfigurefreq_gry_disp = log(1+abs(freq_gry));imshow(mat2gray(freq_gry_disp));title("Image in Freq Domain");% Create LPF Mask [r,c] = size(gry);origin_r = r/2;origin_c = c/2;filtr = zeros(r,c);Dist = 50;for i=1:r  for j=1:c    if ((i-origin_r)^2 + (j-origin_c)^2)^(0.5) <= Dist      filtr(i,j) = 1;    end  endendfigureimshow(uint8(255*filtr));title("LPF Mask");% Apply mask on the image in freq domainresult = freq_gry.*filtr;figureresult_disp = log(1+abs(result));imshow(mat2gray(result_disp));title("LPF on the image");% Inverse Fourier Transforminv_result = ifft2(ifftshift(result));figureimshow(uint8(abs(inv_result)));title("Transformed Image");