clear all;clc;% import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegray_img = double(rgb2gray(img)); N = length(gray_img(:));MP = 255;hist = zeros(MP);cdf = zeros(MP);%m,n = size(gray_img);%for i = 0:(m*n)%  for j = 0:(m*n)%    hist[gray_img[i,j]] = hist[gray_img[i,j]] + 1;%  endfor;%endfor;his = imhist(gray_img, 255);cdf(1) = his(1);for i = 2:MP  cdf(i) = cdf(i-1) + his(i);endfor;cutoff1 = round(0.05 .* N);cutoff2 = round(0.95 .* N);CH = cumsum(his);a = find(CH >= cutoff1);a = a(1)-1;b = find(CH >= cutoff2);b = b(1)-1;R = b-a;scale = R/MP;p = (gray_img - a) .* scale;p = round(p);figure; imshow(uint8(gray_img)); title("Original");figure; imshow(uint8(p)); title("Contrast Stretched"); 