clear all;clc;pkg load image;img = imread('lena.png');img_noise = imnoise(img,'salt & pepper',0.02);my_filter = [1 1 1;1 1 1;1 1 1];img_blur = imfilter(img, my_filter);brisque_img = brisque(img);brisque_noisy = brisque(img_noise);brisque_blur = brisque(img_blur);fprintf('BRISQUE score for original image is %0.4f.\n', brisque_img);fprintf('BRISQUE score for noisy image is %0.4f.\n', brisque_noisy);fprintf('BRISQUE score for blurred image is %0.4f.\n', brisque_blur);figure; imshow(img); title('Original Image');figure; imshow(img_noise); title('Noisy Image');figure; imshow(img_blur); title('Blurry Image');