clear all;clc;% import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegray_img = double(rgb2gray(img)); MP = 255;a = min(gray_img(:));b = max(gray_img(:));R = b-a;scale = R/MP;p = (gray_img - a) .* scale;p = round(p);figure; imshow(uint8(gray_img)); title("Original");figure; imshow(uint8(p)); title("Contrast Stretched");