clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert to grayscalegry = rgb2gray(img);% Sobel edge detection%sobel = edge(gry, "Sobel");% Vertical sobel_h = [-1 -5 -1;0 0 0;1 5 1];% Verticalsobel_v = [-1 0 1;-5 0 5;-1 0 1];result_h = imfilter(gry, sobel_h);result_v = imfilter(gry, sobel_v);%imshow(result);%title("Prewitt Edge Detection"); imwrite(result_h, "results/horizontal_sobel.jpg");imwrite(result_v, "results/vertical_sobel.jpg");% Prewitt edge detection%prewitt = edge(gry, "Prewitt");% Vertical prewitt_h = [-1 -1 -1;0 0 0;1 1 1];% Verticalprewitt_v = [-1 0 1;-1 0 1;-1 0 1];result_h = imfilter(gry, prewitt_h);result_v = imfilter(gry, prewitt_v);%imshow(result);%title("Prewitt Edge Detection"); %imwrite(result_h, "results/horizontal_prewitt.jpg");%imwrite(result_v, "results/vertical_prewitt.jpg");% roberts edge detection%roberts = edge(gry, "Roberts");%imshow(roberts);%title("Roberts Edge Detection");% canny edge detection%canny = edge(gry, "Canny");%imshow(canny);%title("Canny Edge Detection");