clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegry = double(rgb2gray(img)); % low pass filter%myfilter = (1/9)*ones(3,3);% high pass filter%myfilter = [-1,2,-1];% 3x3 high pass filter%myfilter = [-1 -2 -1;0 0 0; 1 2 1];% Mean filter%myfilter = (1/9)*ones(3,3);% Weighted average filter%myfilter = (1/13) * [1 1 1; 1 5 1; 1 1 1];% Gaussian filter%myfilter = (1/273) * [1 4 7 4 1; 4 16 26 16 4; 7 26 41 26 7; 4 16 26 16 4; 1 4 7 4 1];% apply filter on gray imageresult = imfilter(gry, myfilter);% median filter%result = medfilt2(gry, 5);% plot original gray imagefiguresubplot(1,2,1);imshow(uint8(gry));% plot filtered gray imagesubplot(1,2,2);imshow(uint8(result));imwrite(uint8(result), "results/gaussian_filter.jpg");