clear all;close all;clc;pkg load image;img = imread("cur.jpg");gry = double(rgb2gray(img));% low pass filter%myfilter = (1/49)*ones(7,7);% high pass filtermyfilter = [-1,2,-1];% 3x3 high pass filter%myfilter = [-1 -2 -1;0 0 0; 1 2 1];result = imfilter(gry, myfilter);% median filter%result = medfilt2(gry, 5);figuresubplot(1,2,1);imshow(uint8(gry));subplot(1,2,2);imshow(uint8(result));