clear all;close all;clc;pkg load image;gry = double(rgb2gray(imread("lena.png")));% Show Original Imagefigureimshow(uint8(gry));title("Original Image");imwrite(uint8(gry), "results/frequency_domain/LPF/original.jpg");% Convert into Freq Domainfreq_gry = fft2(gry);imwrite(mat2gray(freq_gry), "results/frequency_domain/LPF/fft.jpg");freq_gry = fftshift(freq_gry);imwrite(mat2gray(freq_gry), "results/frequency_domain/LPF/fft_shift.jpg");% Show image in freq domainfigurefreq_gry_disp = log(1+abs(freq_gry));imshow(mat2gray(freq_gry_disp));title("Image in Freq Domain");imwrite(mat2gray(freq_gry_disp), "results/frequency_domain/LPF/fft+fft_shift.jpg");% Create LPF Mask [r,c] = size(gry);origin_r = r/2;origin_c = c/2;filtr = zeros(r,c);Dist = 50;for i=1:r  for j=1:c    if ((i-origin_r)^2 + (j-origin_c)^2)^(0.5) <= Dist      filtr(i,j) = 1;    end  endendfigureimshow(uint8(255*filtr));title("LPF Mask");imwrite(uint8(255*filtr), "results/frequency_domain/LPF/lpf_mask.jpg");% Apply mask on the image in freq domainresult = freq_gry.*filtr;figureresult_disp = log(1+abs(result));imshow(mat2gray(result_disp));title("LPF on the image");imwrite(mat2gray(result_disp), "results/frequency_domain/LPF/lpf_mask_on_fft.jpg");% Inverse Fourier Transforminv_result = ifftshift(result);imwrite(mat2gray(inv_result), "results/frequency_domain/LPF/inv_fft_shift.jpg");inv_result = ifft2(inv_result);imwrite(uint8(abs(inv_result)), "results/frequency_domain/LPF/inv_fft.jpg");