clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegrayscale_img = double(rgb2gray(img)); % show grayscale imagefigureimshow(uint8(grayscale_img));title("grayscale image");# calculate negative of the imageoutput = 255 - grayscale_img;% show output imagefigureimshow(uint8(output));title("output image");