clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy_2 = imnoise(ref, 'salt & pepper', 0.2);noisy_5 = imnoise(ref, 'salt & pepper', 0.5);# average difference implementation[M N] = size(noisy_2);diff = (double(ref) - double(noisy_2));err1 = sum(sum(diff)) / (M*N);[M N] = size(noisy_5);diff = (double(ref) - double(noisy_5));err2 = sum(sum(diff)) / (M*N);figure; imshow(ref); title("Original Image");imwrite(ref, "results/Original.jpg");figure; imshow(noisy_2); title("Noisy Image");imwrite(noisy_2, "results/s&p_noise_0.2.jpg");figure; imshow(noisy_5); title("Noisy Image");imwrite(noisy_5, "results/s&p_noise_0.5.jpg");fprintf('\n The mean-squared error1 is %0.4f\n', err1);fprintf('\n The mean-squared error2 is %0.4f\n', err2);