clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy = imnoise(ref, 'salt & pepper', 0.2);# average difference implementation[M N] = size(noisy);diff = (double(ref) - double(noisy));err = sum(sum(diff)) / (M*N);figure; imshow(ref); title("Original Image");figure; imshow(noisy); title("Noisy Image");fprintf('\n The average difference is %0.4f\n', err);