clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy = imnoise(ref, 'salt & pepper', 0.2);# maximum difference implementation[M N] = size(noisy);diff = (double(ref) - double(noisy));err = max(max(diff));figure; imshow(ref); title("Original Image");figure; imshow(noisy); title("Noisy Image");fprintf('\n The maximum difference is %0.4f\n', err);