clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy = imnoise(ref, 'salt & pepper', 0.2);# in-built mse function%err = immse(noisy, ref);# MSE implementation[M N] = size(noisy);diff = (double(ref) - double(noisy)) .^ 2;err = sum(sum(diff)) / (M*N);figure; imshow(ref); title("Original Image");imwrite(ref, "results/Original.jpg");figure; imshow(noisy); title("Noisy Image");imwrite(noisy, "results/s&p_noise_0.2.jpg");fprintf('\n The mean-squared error is %0.4f\n', err);