clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy_2 = imnoise(ref, 'salt & pepper', 0.2);noisy_5 = imnoise(ref, 'salt & pepper', 0.5);# in-built psnr function%psnr = psnr(ref);# MSE implementation[M N] = size(noisy_2);diff = (double(ref) - double(noisy_2)) .^ 2;err1 = sum(sum(diff)) / (M*N);[M N] = size(noisy_5);diff = (double(ref) - double(noisy_5)) .^ 2;err2 = sum(sum(diff)) / (M*N);# psnr implementationpsnr1 = 10*log10(255^2 / err1);psnr2 = 10*log10(255^2 / err2);figure; imshow(ref); title("Original Image");imwrite(ref, "results/Original.jpg");figure; imshow(noisy_2); title("Noisy Image");imwrite(noisy_2, "results/s&p_noise_0.2.jpg");figure; imshow(noisy_5); title("Noisy Image");imwrite(noisy_5, "results/s&p_noise_0.5.jpg");fprintf('\n The psnr1 is %0.4f\n', psnr1);fprintf('\n The psnr2 is %0.4f\n', psnr2);