clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegry = rgb2gray(img);% Laplacian filter% Positive laplacian_p = [0 1 0; 1 -5 1; 0 1 0];% Negativelaplacian_n = [0 -1 0; -1 5 -1; 0 -1 0];result_p = imfilter(gry, laplacian_p);result_n = imfilter(gry, laplacian_n);%figure;%imshow(result);%title("Prewitt Edge Detection"); imwrite(result_p, "results/laplacian_positive.jpg");imwrite(result_n, "results/laplacian_negative.jpg");