clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegrayscale_img = double(rgb2gray(img)); % show grayscale imagefigureimshow(uint8(grayscale_img));title("grayscale image");% Power Law Transformation (Gamma Correction)c=0.3;gamma=1.5;result = c*grayscale_img.^gamma;% plot imagefigureimshow(uint8(result));title("transformed image");