clear all;clc;% import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegray_img = rgb2gray(img); %MP = 255;%a = min(gray_img(:));%b = max(gray_img(:));%R = b-a;%scale = R/MP;%p = (gray_img - a) .* scale;%p = round(p);cs = imadjust(gray_img, stretchlim(gray_img),[]);h1 = hist(uint8(gray_img));h2 = hist(uint8(cs));figure; imshow(uint8(gray_img)); title("Original");figure; imshow(uint8(cs)); title("Contrast Stretched");figure; imhist(uint8(gray_img)); title("Histogram of Original Image");figure; imhist(uint8(cs)); title("Histogram of Output Image");imwrite(uint8(cs), "results/contrast_stretched.jpg");