clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegrayscale_img = double(rgb2gray(img)); % show grayscale imagefigureimshow(uint8(grayscale_img));title("grayscale image");imwrite(uint8(grayscale_img), "results/original.jpg");# calculate negative of the imagec = 45;output = c * log(1 + grayscale_img);% show output imagefigureimshow(uint8(output));title("output image");imwrite(uint8(output), "results/log_tansformation.jpg");