close all; % close all plotsclear all; % clear all variablesclc;       % clear command windowpkg load image;  orig = imread('lena.png');grayimg = rgb2gray(orig);%b = orig>200;  % threshold%imshow(b);figuresubplot(2,2,1);imshow(grayimg);title("gray image","fontsize",15,"color",[1,0,0]);subplot(2,2,2);imhist(grayimg);title("histogram","fontsize",15);result = histeq(double(grayimg)/255,50);subplot(2,2,3);imshow(uint8(result*255));title("histogram equalized image","fontsize",15);